import React, { useState } from 'react';

function AdviceComponent () {
    const [advice, displayAdvice] = useState ();
    useEffect (() => {
        fetch ("https://api.adviceslip.com/advice")
        .then(res => res.json())
        .then()
    }) 


}


export default AdviceComponent
